import React from 'react'
import styled from 'styled-components'

const Hero = styled.section`
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-top: 6rem;
  position: sticky;
  top: 10rem;
  height: fit-content;

  p {
    text-align: left;
    max-width: 400px;
    font-size: 18px;
  }

  .title {
    text-align: left;
    max-width: 450px;
    font-size: 48px;
    line-height: 125%;
    letter-spacing: 0.002em;
    color: #1f1f1f;
    margin: 0;
    font-family: 'MatterSQ-Medium';
  }

  .icon {
    width: 48px;
  }

  .list {
    max-width: 960px;
  }

  a {
    color: #131313;
    font-family: 'MatterSQ-SemiBold';
  }

  .ethereum {
    color: #313896;
    font-family: 'MatterSQ-Regular';
  }

  @media screen and (max-width: 960px) {
    position: relative;
    top: initial;
    margin-top: 2rem;

    .title {
      font-size: 35px;
    }
  }
`

const HoverLink = styled.a`
  transition: box-shadow 0.25s ease, translate 0.25s ease;
  margin-top: 0.5rem;
  width: fit-content;
  :hover {
    box-shadow: -6px 6px 0px #d6fdff;
    translate: 1px -1px;
  }
`

export default function Header() {
  return (
    <Hero>
      <span style={{ marginBottom: '1rem' }}>
        An{' '}
        <a href="https://ethereum.org/en/" className="uniswap">
          Ethereum
        </a>{' '}
        Public Good
      </span>

      <p className="title">An up-to-date collection of ERC20 icons.</p>

      <p style={{ fontSize: '20px', lineHeight: '150%' }} className="description" id="why-lists">
        Token Icons is a community-led initiative to source and maintain ERC20 icons in a transparent and
        highly-accessible way.
      </p>
      <HoverLink target="_blank" rel="noopener noreferrer" href="https://uniswap.org/blog/token-lists/">
        {'->'} Why token icons?
      </HoverLink>
      <HoverLink
        target="_blank"
        rel="noopener noreferrer"
        href="https://github.com/Uniswap/token-lists#authoring-token-lists"
      >
        {'->'} Add your own
      </HoverLink>

      <HoverLink target="_blank" rel="noopener noreferrer" href="http://community.tokenlists.org/">
        {'->'} Use it in your dapp
      </HoverLink>
      <HoverLink target="_blank" rel="noopener noreferrer" href="http://community.tokenlists.org/">
        {'->'} Community
      </HoverLink>
    </Hero>
  )
}
